SET NAMES UTF8;

DROP DATABASE IF EXISTS star;

CREATE DATABASE star CHARSET=UTF8;

USE star;

CREATE TABLE star_user(
    uid INT PRIMARY KEY AUTO_INCREMENT,
    uname VARCHAR(10),          
    upwd int,
    email VARCHAR(32),
    gender VARCHAR(10) 
);
CREATE TABLE star_peaple(
    pid INT PRIMARY KEY AUTO_INCREMENT,
    star_name VARCHAR(64),
    star_ename VARCHAR(64),
    category VARCHAR(32),
    introductio TEXT,
    star_profile TEXT,
    area  VARCHAR(16)
);

