CREATE TABLE star_photo(
    lid  INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(128),
    cover VARCHAR(128),
    imgs  VARCHAR(800),
    category VARCHAR(16),
    star_name VARCHAR(64)
);


create table news (
    nid int PRIMARY KEY AUTO_INCREMENT,
    ntitle  VARCHAR(128),
